sys_script_client.script:
function onSubmit() {
   // The execution of the script is on save or submit 
	var assignment_group = g_form.getValue('assignment_group');
	var state = g_form.getValue('state');
   // Check if the state has changed to "Closed" (resolved)
	if (state == '3'){
		// Copies the value from Assignment Group
		g_form.setValue('resolution_group_1', assignment_group);
	}
	else{
		// Clear all from the Resolution Group 1 field 
		g_form.clearValue('resolution_group_1');
	}
}
