sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   // Instantiate the GetCIClass Script Include 
	var getCICl = new GlideAjax('GetCIClass');
	// Specify the getCI method
	getCICl.addParam('sysparm_name','getClass');
	// Pass the Requested for cmdb_ci
	getCICl.addParam('sysparm_CI', g_form.getValue('cmdb_ci'));
	// Send the request to the server
	getCICl.getXML(populateClassField);


	// When the response is back from the server
	function populateClassField(response){
		// Extract the class from the response, clear any value from the class field, 
		// set new value in the class field
		var classFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_class');
		g_form.setValue('u_class',classFromScriptInclude);
	}

}
