sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

    // Instantiate the CountCIsInClass Script Include 
	var countCI = new GlideAjax('CountCIsInClass');
	// Specify the countCI method
	countCI.addParam('sysparm_name','countCIs');
	// Pass the Requested for cmdb_ci
	countCI.addParam('sysparm_class', newValue);
	// Send the request to the server
	countCI.getXMLAnswer(function(answer){
		alert('Total ' + answer + ' CIs of the same class exist in the system');
	});


	// When the response is back from the server
	//function CountCIInClass(response){
		// Extract the number of CI's from the response, and display it 
	//	var countCIFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
	//	alert(g_form.addInfoMessage('Total' + countCIFromScriptInclude + 'CIs of thesame class exist in the system'));
//	}

}
