sys_script_include.script:
var GetCIClass = Class.create();
GetCIClass.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	// Define the getClass function.  
	// Create a GlideRecord for the cmdb_ci table.
	// Use the sysparm_CI passed from the client side to retrieve a record from the CMDB table.
	// Return the calss for the requested record
	getClass: function() {
		var CIClass = new GlideRecord("cmdb_ci");
		CIClass.get(this.getParameter('sysparm_CI'));
		return CIClass.sys_class_name + '';
	},
    type: 'GetCIClass'
});
