sys_script_include.script:
var CountCIsInClass = Class.create();
CountCIsInClass.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	// Define the countCIs function.  
	// Create a GlideAggregate for the cmdb_ci table.
	// Use the sysparm_class passed from the client side to retrieve a record from the CMDB table.
	// Return the CIs number for the class
	//getClass: function() {
	countCIs: function() {
		var CIClass = new GlideRecord("cmdb_ci");
		CIClass.get(this.getParameter('sysparm_CI'));
		var count = new GlideAggregate("cmdb_ci");
		count.addAggregate('COUNT',CIClass.getCategory('Class') );
		count.query();
		var CIs = 0;
		if(count.next()) {
			CIs = count.getAggregate('COUNT',CIClass.getCategory('Class'));
		}
		return CIs;
		//return CIClass.sys_class_name + '';
	},
    //type: 'GetCIClass'
	type: 'CountCIsInClass'
});
